import React, { useState, useRef } from "react";
import axios from "axios";
import Lottie from "react-lottie";
// Redux
import { setAlert } from "../../store/actions/Index";
// Comoponets
import Alert from "../../layout/Alert/Alert";
import * as loadingLogo from "../../assets/logos/page-loading.json";
import "./ResetPassword.css";

const ResetPassword = ({ match }) => {
  const [emailLoading, setEmailLoading] = useState(false);
  const [formData, setFormData] = useState({
    password: "",
    passwordMatch: "",
  });
  const { password, passwordMatch } = formData;
  const animation = useRef(null);

  const defaultOptions = {
    loop: true,
    animationData: loadingLogo.default,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    await setEmailLoading(true);
    e.preventDefault();
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    if (password === passwordMatch) {
      let body = await JSON.stringify({
        password: password,
        token: match.params.token,
      });
      try {
        await axios.post("/api/auth/reset_password/:token", body, config);
        await setFormData({ ...formData, password: "", passwordMatch: "" });
        await setAlert(`Password updated`, "success");
        setEmailLoading(false);
      } catch (err) {
        const errors = err.response.data.errors;
        if (errors) {
          errors.forEach((error) => setAlert(error.msg, "danger"));
        }
      }
      setEmailLoading(false);
    } else {
      setAlert(`Passwords do not match`, "danger");
    }
  };

  return (
    <div className="resetFormPageContainer">
      <form
        onSubmit={(e) => onSubmit(e)}
        className="accountPasswordPageFormContainer"
      >
        <h2 className="passwordPageTitleText">Enter your new password below</h2>
        <Alert />
        {emailLoading ? (
          <Lottie
            options={defaultOptions}
            height={200}
            width={200}
            ref={animation}
          />
        ) : (
          <>
            {" "}
            <input
              type="password"
              title="Enter new password"
              value={password}
              onChange={(e) => onChange(e)}
              name="password"
              className="accountPasswordPageFormInput"
              placeholder="New Password"
            />
            <input
              type="password"
              title="Re-enter new password"
              value={passwordMatch}
              onChange={(e) => onChange(e)}
              name="passwordMatch"
              className="accountPasswordPageFormInput"
              placeholder="Re-enter New Password"
            />
            <input
              type="submit"
              className="resetPasswordPageBtn"
              title="Submit"
            />
          </>
        )}
      </form>
    </div>
  );
};

export default ResetPassword;
