import { LOG_IN, LOG_OUT } from "../actions/ActionTypes";

export const initialState = {
  isLoggedIn: true,
};

const AuthenticateReducer = (state = initialState, action) => {
  switch (action.type) {
    case LOG_IN:
      return {
        ...state,
        isLoggedIn: true
      };
    case LOG_OUT:
      return {
        ...state,
        isLoggedIn: false
      };
    default:
      return state;
  }
};

export default AuthenticateReducer;
