const setVideoIndex = (videos, currentUrl, VideoIndexRedux) => {
  if (window.location.href.split("/")[3]) {
    for (let i = 0; i < videos.length; i++) {
      if (videos[i]._id === currentUrl) return i;
    }
  } else {
    return VideoIndexRedux;
  }
  return 0;
};

export default setVideoIndex;
