import React from "react";
import { connect } from "react-redux";
import { Link, useLocation } from "react-router-dom";
import ReactTooltip from "react-tooltip";

import { userSavedIndex, delSavedVid } from "../../../store/actions/Index";
import "./SavedVids.scss";

const SavedVids = ({
  userSavedIndex,
  delSavedVid,
  index,
  _id,
  thumb,
  title,
}) => {
  let location = useLocation();

  const setindex = (index) => userSavedIndex(index);

  const delVid = (_id) => {
    console.log("ID", _id);
    delSavedVid(_id);
  };

  return (
    <div>
      <div style={{ color: "white", textDecoration: "none" }}>
        <li className="userSavedVidsContainer">
          <Link
            onClick={() => setindex(index)}
            to={{
              pathname: `/saved/${_id}`,
              state: { background: location },
            }}
          >
            <img src={thumb} className="profileThumbNail" alt="" />
          </Link>
          <div className="userSavedVidTitle">
            <Link
              onClick={() => setindex(index)}
              to={{
                pathname: `/saved/${_id}`,
                state: { background: location },
              }}
              style={{ color: "white", textDecoration: "none" }}
            >
              {title.length >= 70 ? `${title.slice(0, 66)}...` : title}
            </Link>
            <div
              data-for="delClip"
              className="savedVidDeleteBtn"
              onClick={(_id) =>
                window.confirm("Are you sure you wish to remove this clip?") &&
                delVid(_id)
              }
            >
              x
            </div>
            <ReactTooltip
              id="delClip"
              place="bottom"
              effect="solid"
              delayHide={100}
              delayShow={300}
              border
              multiline={false}
            >
              Delete Clip?
            </ReactTooltip>
          </div>
        </li>
      </div>
    </div>
  );
};

export default connect(null, { userSavedIndex, delSavedVid })(SavedVids);
