import { SET_ALERT, REMOVE_ALERT } from "./ActionTypes";

export const setAlert = (msg, alertType) => dispatch => {
  dispatch({
    type: SET_ALERT,
    payload: { msg }
  });

  setTimeout(() => {
    dispatch({ type: REMOVE_ALERT });
  }, 5000);
};
