import React, { useState } from "react";
import { connect } from "react-redux";
// Redux
import { register } from "../../../../store/actions/Index";
// Components
import "./RegisterForm.scss";

const RegisterForm = ({ register, errors }) => {
  const [emailError, setEmailError] = useState(errors);
  const [formData, setFormData] = useState({
    name: "",
    email: "",
    password: "",
    password2: ""
  });

  const { name, email, password, password2 } = formData;

  const onChange = e =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = e => {
    e.preventDefault();
    if (password !== password2) {
      setEmailError("Passwords do not match");
    } else {
      register({ name, email, password });
    }
  };

  return (
    <div className="registerFormContainer">
      <form onSubmit={e => onSubmit(e)}>
        <div>
          <input
            type="text"
            placeholder="Name"
            name="name"
            value={name}
            onChange={e => onChange(e)}
            required
            className="registerFormEmail"
          />
        </div>
        <div>
          <input
            type="email"
            placeholder="Email Address"
            name="email"
            className="registerFormEmail"
            value={email}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Password"
            name="password"
            minLength={6}
            className="registerFormEmail"
            value={password}
            onChange={e => onChange(e)}
            required
          />
        </div>
        <div>
          <input
            type="password"
            placeholder="Confirm Password"
            name="password2"
            minLength={6}
            className="registerFormEmail"
            value={password2}
            onChange={e => onChange(e)}
          />
          <div className="loginFormError">{emailError}</div>
        </div>
        <div className="formBtnContainer">
          <input type="submit" className="submitBtn" value="Lets go" />
        </div>
      </form>
      {/* <SubmitBtn handleSubmit={e => onSubmit(e)} /> */}
    </div>
  );
};

//  Redux
const mapStateToProps = state => {
  return {
    mouseMoving: state.MouseMoving.mouseMoving,
    errors: state.Authenticate.errors
  };
};

export default connect(
  mapStateToProps,
  { register }
)(RegisterForm);
