import { SET_ALERT, REMOVE_ALERT } from "./ActionTypes";

export const setAlert = (msg, alertType) => dispatch => {
  let id = Math.random();
  dispatch({
    type: SET_ALERT,
    payload: { msg, id }
  });

  setTimeout(() => {
    dispatch({ type: REMOVE_ALERT, payload: id });
  }, 5000);
};
