import React from "react";
import { connect } from "react-redux";

const Alert = ({ alerts }) =>
  alerts !== null &&
  alerts.length > 0 &&
  alerts.map(alert => (
    <div key={alert.id} style={{ fontSize: 20, color: "red" }}>
      {alert.msg}
    </div>
  ));

const mapStateToProps = state => ({
  alerts: state.Authenticate.errors
});

export default connect(mapStateToProps)(Alert);
