import React from "react";
import { Link, useLocation } from "react-router-dom";
import { connect } from "react-redux";

import { modalOpen } from "../../store/actions/Index";
import "./ModalOpenBtn.scss";

const ModalOpenBtn = ({ modalOpen, open }) => {
  let location = useLocation();

  console.log(location.pathname);

  return (
    <Link
      to={{
        pathname: location.pathname.match(`/${"u2"}/profile/categories`)
          ? `/${"u2"}/profile/categories`
          : "/categories",
        state: { background: location }
      }}
      className="HamburgerContainer"
      onClick={() => modalOpen()}
    >
      <div className="Hamburger" onClick={open}>
        <b></b>
        <b></b>
        <b></b>
      </div>
    </Link>
  );
};

export default connect(
  null,
  { modalOpen }
)(ModalOpenBtn);
