import React, { useState, useRef } from "react";
import axios from "axios";
import { connect } from "react-redux";
import Lottie from "react-lottie";
// Redux
import { setAlert } from "../../store/actions/Index";
// Comoponets
import Alert from "../../layout/Alert/Alert";
import SignInForm from "../LoginModal/RegisterForm/SignInForm/SignInForm";
import * as loadingLogo from "../../assets/logos/page-loading.json";
import "./ResetPassword.css";

const ResetPassword = ({ match, setAlert }) => {
  const [passwordUpdated] = useState(false);
  const [emailLoading, setEmailLoading] = useState(false);
  const [formData, setFormData] = useState({
    password: "",
    passwordMatch: "",
  });
  const { password, passwordMatch } = formData;
  const animation = useRef(null);

  const defaultOptions = {
    loop: true,
    animationData: loadingLogo.default,
    rendererSettings: {
      preserveAspectRatio: "xMidYMid slice",
    },
  };

  const onChange = (e) =>
    setFormData({ ...formData, [e.target.name]: e.target.value });

  const onSubmit = async (e) => {
    e.preventDefault();
    await setEmailLoading(true);
    const config = {
      headers: {
        "Content-Type": "application/json",
      },
    };

    if (password === passwordMatch) {
      if (password.length === 0 || passwordMatch.length === 0)
        setAlert("Please enter password", "danger");
      let body = await JSON.stringify({
        password: password,
        token: match.params.token,
      });
      try {
        await axios.post("/api/auth/reset_password/:token", body, config);
        await setFormData({ ...formData, password: "", passwordMatch: "" });
        await setAlert(`Password updated`, "success");
        setEmailLoading(false);
      } catch (err) {
        const errors = err.response.data.errors;
        if (errors) {
          errors.forEach((error) => setAlert(error.msg, "danger"));
        }
      }
      setEmailLoading(false);
    } else {
      await setAlert(`Passwords do not match`, "danger");
      setEmailLoading(false);
    }
  };

  return (
    <div className="resetFormPageContainer">
      <form
        onSubmit={(e) => onSubmit(e)}
        className="accountPasswordPageFormContainer"
      >
        <Alert />
        {!passwordUpdated ? (
          <>
            <h2 className="passwordPageTitleText">
              Password changed, please log in below
            </h2>
            <SignInForm showChangePassword={false} />
          </>
        ) : (
          <>
            {" "}
            <h2 className="passwordPageTitleText">
              Enter your new password below
            </h2>
            {emailLoading ? (
              <Lottie
                options={defaultOptions}
                height={200}
                width={200}
                ref={animation}
              />
            ) : (
              <>
                <input
                  type="password"
                  title="Enter new password"
                  value={password}
                  onChange={(e) => onChange(e)}
                  name="password"
                  className="accountPasswordPageFormInput"
                  placeholder="New Password"
                  minLength="6"
                />
                <input
                  type="password"
                  title="Re-enter new password"
                  value={passwordMatch}
                  onChange={(e) => onChange(e)}
                  name="passwordMatch"
                  className="accountPasswordPageFormInput"
                  placeholder="Re-enter New Password"
                  minLength="6"
                />
                <input
                  type="submit"
                  className="resetPasswordPageBtn"
                  value="Submit"
                />
              </>
            )}
          </>
        )}
      </form>
    </div>
  );
};

export default connect(null, { setAlert })(ResetPassword);
