import React, { useState } from "react";
import { connect } from "react-redux";
// Redux vars
import { logIn } from "../../../../store/actions/Index";
// My components
import SubmitBtn from "../SubmitBtn/SubmitBtn";
import "./RegisterForm.scss";

const RegisterForm = props => {
  const [email, setEmail] = useState("");
  const [emailError, setEmailError] = useState("");
  const [password, setPassword] = useState("");
  const [passwordError, setPasswordError] = useState("");
  const [passwordMatch, setPasswordMatch] = useState("");

  const validate = () => {
    let emailError = "";
    let passwordMatchError;
    if (!email.includes("@")) {
      emailError = "Invalid email";
    }
    if (password !== passwordMatch) {
      passwordMatchError = "Passwords do not match";
    }
    if (!password) {
      passwordMatchError = "Please enter a password";
    }
    if (emailError || passwordMatchError) {
      setEmailError(emailError);
      setPasswordError(passwordMatchError);
      return false;
    }
    return true;
  };

  const handleChange = e => {
    setEmail(e.target.value);
  };

  const handleSubmit = e => {
    e.preventDefault();
    const isValid = validate();
    if (isValid) {
      setEmail("");
      setPassword("");
      setEmailError("");
      setPasswordError("");
      setPasswordMatch("");
      props.onLogIn();
    }
  };

  return (
    <div className="registerFormContainer">
      <form onSubmit={handleSubmit}>
        <div>
          <input
            className="registerFormEmail"
            placeholder="Email"
            type="email"
            value={email}
            onChange={handleChange}
          />
          <div className="loginFormError">{emailError}</div>
        </div>
        <div>
          <input
            className="registerFormEmail"
            placeholder="Password"
            type="password"
            value={password}
            onChange={e => setPassword(e.target.value)}
          />
        </div>
        <div>
          <input
            className="registerFormEmail"
            placeholder="Re-enter Password"
            type="password"
            value={passwordMatch}
            onChange={e => setPasswordMatch(e.target.value)}
          />
          <div className="loginFormError">{passwordError}</div>
        </div>
      </form>
      <SubmitBtn handleSubmit={handleSubmit} />
    </div>
  );
};

//  Redux mapping
const mapDispatchToProps = dispatch => {
  return {
    onLogIn: () => dispatch(logIn())
  };
};

export default connect(
  null,
  mapDispatchToProps
)(RegisterForm);
